package org.jeff.springboot.bootcore.res;public enum HttpStatusCode {    SUCCESS(200, "调用成功"),    NOT_DATA(202, "调动成功,但是没有数据"),    PARAMS_ERROR(403, "传入的参数有误"),    NOT_LOGIN(400, "没有权限访问"),    TOO_FREQUENT(445, "调用太频繁"),    EXCEPTION(500, "发生异常"),    SYS_ERROR(501, "系统错误"),    EXIST_VALUE(901, "该值在系统中已经存在");    HttpStatusCode(int code, String message) {        this.code = code;        this.message = message;    }    private int code;    private String message;    public int getCode() {        return code;    }    public String getMessage() {        return message;    }}